import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const RegisterForm = () => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    username: '',
    pass_phrase: '',
    photo: null,
  });

  const [message, setMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false); // State to control the popup visibility
  const useNavigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      photo: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const form = new FormData();
    form.append('first_name', formData.first_name);
    form.append('last_name', formData.last_name);
    form.append('username', formData.username);
    form.append('pass_phrase', formData.pass_phrase);
    if (formData.photo) {
      form.append('photo', formData.photo);
    }

    try {
      const response = await fetch('http://127.0.0.1:8000/register', {
        method: 'POST',
        body: form,
      });

      console.log("Response status:", response.status);

      const text = await response.text();
      setMessage(text);

      if (response.status === 200) {
        setShowPopup(true); // Show the popup with the message

        setTimeout(() => {
          setShowPopup(false); // Hide the popup after a delay
          navigate('/login'); // Redirect to login page
        }, 3000); // Adjust the delay as needed
      }

    } catch (error) {
      console.error('Error:', error);
      setMessage('An error occurred');
      setShowPopup(true); // Show the popup with the error message
    }
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="first_name"
          placeholder="First Name"
          value={formData.first_name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="last_name"
          placeholder="Last Name"
          value={formData.last_name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="pass_phrase"
          placeholder="Pass Phrase"
          value={formData.pass_phrase}
          onChange={handleChange}
          required
        />
        <input type="file" name="photo" onChange={handleFileChange} />
        <button type="submit">Register</button>
      </form>
      {showPopup && (
        <div className="message-popup">
          <button className="close-btn" onClick={() => setShowPopup(false)}>X</button>
          <p>{message}</p>
        </div>
      )}
      <p>
        Already have an account? <Link to="/login">Login here</Link>
      </p>
    </div>
  );
};

export default RegisterForm;
