import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Forms.css';

const LoginForm = () => {
  const [formData, setFormData] = useState({
    pass_phrase: '',
    photo: null,
  });

  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      photo: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const form = new FormData();
    form.append('pass_phrase', formData.pass_phrase);
    if (formData.photo) {
      form.append('photo', formData.photo);
    }

    try {
      const response = await fetch('http://127.0.0.1:8000/authenticate', {
        method: 'POST',
        body: form,
    });

    console.log("Response status:", response.status);

      if (response.status === 200) {
          const result = await response.text(); // Get the response text
  
          if (result === "Wrong passphrase") {
              setMessage("Wrong passphrase"); // Display wrong passphrase message
          } else {
              localStorage.setItem("username", result); // Store the username for later usage
              console.log(result);
              navigate(`/welcome/${result}`);
          }
      } else {
          setMessage("Login failed");
      }
      } catch (error) {
        setMessage("There is an error : " + error);
      }   
  };

  return (
    <>
    <div className="top-images-container">
        <img src="../../public/ensaj.png" alt="First descriptive text" />
        <div className="vertical-line"></div>
        <img src="../../public/3dsm.png" alt="Second descriptive text" />
      </div>
    <div className="container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="password"
          name="pass_phrase"
          placeholder="Pass Phrase"
          value={formData.pass_phrase}
          onChange={handleChange}
          required
        />
        <input type="file" name="photo" onChange={handleFileChange} required />
        <button type="submit">Login</button>
      </form>
      {message && (
        <div className="message-popup">
          <button className="close-btn" onClick={() => setMessage('')}>X</button>
          <p>{message}</p>
        </div>
      )}
      <p>
        Don't have an account? <a href="/register">Register here</a>
      </p>
    </div>
    </>
  );
};

export default LoginForm;
