import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './RegisterForm.css'; // Reuse the same CSS file

const LoginForm = () => {
  const [formData, setFormData] = useState({
    pass_phrase: '',
    photo: null,
  });

  const [message, setMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [showLoading, setShowLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      photo: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setShowLoading(true);

    const form = new FormData();
    form.append('pass_phrase', formData.pass_phrase);
    if (formData.photo) {
      form.append('photo', formData.photo);
    }

    try {
      const response = await fetch('http://127.0.0.1:8000/authenticate', {
        method: 'POST',
        body: form,
      });

      const result = await response.text();
      setMessage(result);

      if (response.status === 200) {
        localStorage.setItem('username', result);
        setShowPopup(true); // Show success popup
        setTimeout(() => {
          navigate(`/welcome/${result}`);
        }, 2000);
      } else {
        setShowPopup(true); // Show failure popup
      }
    } catch (error) {
      console.error('Error:', error);
      setMessage('An error occurred');
    } finally {
      setShowLoading(false);
    }
  };

  return (
    <div className="main-container">
      <div className="left-side">
        <img src="/images/logo.png" alt="Company Logo" className="logo" />
        <p className="mantra">Your Success, Our Commitment</p>
      </div>
      <div className="right-side">
        <div className="card">
          <h2>Login</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="password"
              name="pass_phrase"
              placeholder="Pass Phrase"
              value={formData.pass_phrase}
              onChange={handleChange}
              required
            />
            <input type="file" name="photo" onChange={handleFileChange} required />
            <button type="submit">Login</button>
          </form>
          <p>{message}</p>

          {/* Loading Popup */}
          {showLoading && (
            <div className="loading-popup">
              <p>Processing, please wait...</p>
            </div>
          )}

          {/* Success Popup */}
          {showPopup && (
            <div className="message-popup">
              <p>{message}</p>
              <button className="close-btn" onClick={() => setShowPopup(false)}>X</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
