import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Forms.css'; // Reuse the same CSS file

const LoginForm = () => {
  const [formData, setFormData] = useState({
    pass_phrase: '',
    photo: null,
  });

  const [message, setMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [showLoading, setShowLoading] = useState(false);
  const [showCamera, setShowCamera] = useState(false);
  const [photoTaken, setPhotoTaken] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const startCamera = () => {
    setShowCamera(true);
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
      })
      .catch((err) => {
        console.error('Error accessing the camera:', err);
      });
  };

  const takePicture = () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    const context = canvas.getContext('2d');

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      setFormData({
        ...formData,
        photo: blob,
      });
      setPhotoTaken(true);
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setShowLoading(true);

    const form = new FormData();
    form.append('pass_phrase', formData.pass_phrase);
    if (formData.photo) {
      form.append('photo', formData.photo);
    }

    try {
      const response = await fetch('http://127.0.0.1:8000/authenticate', {
        method: 'POST',
        body: form,
      });

      const result = await response.text();
      setMessage(result);

      if (response.status === 200) {
        localStorage.setItem('username', result);
        setShowPopup(true); // Show success popup
        setTimeout(() => {
          navigate(`/welcome/${result}`);
        }, 2000);
      } else {
        setShowPopup(true); // Show failure popup
      }
    } catch (error) {
      console.error('Error:', error);
      setMessage('An error occurred');
    } finally {
      setShowLoading(false);
    }
  };

  return (
    <div className="main-container">
      <div className="left-side">
        <img src="/images/3dsm.png" alt="3D SMART FACTORY" className="logo" />
        <p className="mantra">Une structure mixte qui va de la recherche à la création des activités socio-économiques en créant des Startups de différents domaines</p>
      </div>
      <div className="right-side">
        <div className="form-card">
          <h2>Login</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="password"
              name="pass_phrase"
              placeholder="Pass Phrase"
              value={formData.pass_phrase}
              onChange={handleChange}
              required
            />
            {showCamera ? (
              <>
                <video ref={videoRef} autoPlay style={{ width: '100%' }}></video>
                <canvas ref={canvasRef} style={{ display: 'none' }}></canvas>
                {!photoTaken ? (
                  <button type="button" onClick={takePicture}>
                    Capture Photo
                  </button>
                ) : (
                  <button type="submit">Login</button>
                )}
              </>
            ) : (
              <button type="button" onClick={startCamera}>
                Open Camera
              </button>
            )}
          </form>
          <p>{message}</p>

          {/* Loading Popup */}
          {showLoading && (
            <div className="loading-popup">
              <p>Processing, please wait...</p>
            </div>
          )}

          {/* Success Popup */}
          {showPopup && (
            <div className="message-popup">
              <p>{message}</p>
              <button className="close-btn" onClick={() => setShowPopup(false)}>X</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
